// FIX: Moved POPULAR_SERVICES here from constants.tsx to resolve a circular dependency.
// FIX: Explicitly use React.ReactElement to resolve "Cannot find namespace 'JSX'" error.
export const POPULAR_SERVICES: { name: string; icon: React.ReactElement }[] = [
  { name: 'Netflix', icon: <NetflixIcon /> },
  { name: 'Spotify', icon: <SpotifyIcon /> },
  { name: 'YouTube Premium', icon: <YoutubeIcon /> },
  { name: 'Adobe Creative Cloud', icon: <AdobeIcon /> },
  { name: 'Notion', icon: <NotionIcon /> },
  { name: 'Figma', icon: <FigmaIcon /> },
  { name: 'Gmail', icon: <GmailIcon /> },
  { name: 'App Store', icon: <AppStoreIcon /> },
  { name: 'Google Drive', icon: <GoogleDriveIcon /> },
  { name: 'TradingView', icon: <TradingViewIcon /> },
  { name: 'Telegram', icon: <TelegramIcon /> },
  { name: 'VK', icon: <VkIcon /> },
  { name: 'ChatGPT', icon: <ChatGptIcon /> },
];

// FIX: Explicitly use React.ReactElement to resolve "Cannot find namespace 'JSX'" error.
export const getServiceIcon = (name: string): React.ReactElement => {
    const service = POPULAR_SERVICES.find(s => name.toLowerCase().includes(s.name.toLowerCase()));
    if(service) return service.icon;

    return (
        <span className="font-bold text-xl text-slate-500 dark:text-slate-400">
            {name.charAt(0).toUpperCase()}
        </span>
    );
};